import React from 'react'
import { useEffect } from 'react';
import { useState } from 'react';
import { useParams } from 'react-router-dom'
import RestaurantFinder from '../apis/RestaurantFinder';

const UpdateRestaurant = (props) => {

    const customStyle = {
        paddingLeft: 10,
        paddingBottom: 5,
        paddingTop: 5, 
    }
  
    const {id} = useParams();
    console.log(id);

    const [name, setName] = useState("");
    const [location, setLocation] = useState("");
    const [priceRange, setPriceRange] = useState("");

    useEffect(() => {
        const fetchData = async () => {
            const response = RestaurantFinder.get(`/${id}`);
            console.log((await response).data.data.restaurant);
        }

        fetchData();
    }, [])

    return (
        <form>
            <div className='form-group'>
                <label style={customStyle} for="name">Name</label>
                <input value={name} onChange={e => setName(e.target.value)} type="text" class="form-control m-2" id="name"></input>
            </div>

            <div className='form-group'>
                <label style={customStyle} for="location">Location</label>
                <input  value={location} onChange={e => setLocation(e.target.value)} type="text" class="form-control m-2" id="location"></input>
            </div>

            <div className='form-group'>
                <label style={customStyle} for="price_range">Price Range</label>
                <select 
                    id="price_range"
                    value={priceRange} 
                    onChange={e => setPriceRange(e.target.value)} 
                    className='form-select m-2'>
                        <option defaultValue>Price Range</option>
                        <option value="1">$</option>
                        <option value="2">$$</option>
                        <option value="3">$$$</option>
                        <option value="4">$$$$</option>
                        <option value="5">$$$$$</option>
                </select>
            </div>

            <button style={{marginTop: 10, marginLeft: 7}} className='btn btn-primary'>Submit</button>
        </form>
    )
}

export default UpdateRestaurant
