import React from 'react'
import { useState } from 'react'

const customStyle = {
    "paddingRight": 20
}

const AddRestaurant = () => {

    // const [name, setName] = useState("");
    // const [location, setLocation] = useState("");
    // const [priceRange, setPriceRange] = useState("");
    const [restaurant, setRestaurant] = useState({
        name: "",
        location: "",
        price_range: ""
    });

    function handleChange(e){
        const key = e.target.name;
        const value = e.target.value
        setRestaurant((prevValue) => {
            return {...prevValue, [key]: value};
        })
    }

    return (
        <div style={customStyle}>
            <div className='row'>
                <div className='form-group col'>
                    <input value={restaurant.name}
                    onChange = {handleChange}
                    className='form-control m-2' type='text' placeholder='Name'>
                    </input>
                </div>

                <div className='form-group col'>
                    <input 
                    value={restaurant.location}
                    onChange={handleChange}
                    className='form-control m-2' type='text' placeholder='Location'>
                    </input>
                </div>

                <div className='form-group col'>
                    <select value={restaurant.price_range} onChange={handleChange} className='form-select m-2'>
                        <option defaultValue>Price Range</option>
                        <option value="1">$</option>
                        <option value="2">$$</option>
                        <option value="3">$$$</option>
                        <option value="4">$$$$</option>
                        <option value="5">$$$$$</option>
                    </select>
                </div>

                <div className="form-group col">
                    <button type="button" className="btn btn-primary form-control m-2">Add</button>
                </div>
            </div>
        </div>
    )
}

export default AddRestaurant
