import React from 'react'
import { useState } from 'react';
import { useParams } from 'react-router-dom'

const UpdateRestaurant = (props) => {

    const customStyle = {
        paddingLeft: 10 
    }
  
    const {id} = useParams();
    console.log(id);

    const [name, setName] = useState("");
    const [location, setLocation] = useState("");
    const [priceRange, setPriceRange] = useState("");

    return (
        <form>
            <div className='form-group'>
                <label style={customStyle} for="name">Name</label>
                <input value={name} onChange={e => setName(e.target.value)} type="text" class="form-control m-2" id="name"></input>
            </div>

            <div className='form-group'>
                <label style={customStyle} for="location">Location</label>
                <input  value={location} onChange={e => setName(e.target.value)} type="text" class="form-control m-2" id="location"></input>
            </div>

            <div className='form-group col'>
                    <select 
                    value={priceRange} 
                    onChange={e => setPriceRange(e.target.value)} 
                    className='form-select m-2'>
                        <option defaultValue>Price Range</option>
                        <option value="1">$</option>
                        <option value="2">$$</option>
                        <option value="3">$$$</option>
                        <option value="4">$$$$</option>
                        <option value="5">$$$$$</option>
                    </select>
            </div>

            <button style={{marginTop: 10, marginLeft: 5}} className='btn btn-primary'>Submit</button>
        </form>
    )
}

export default UpdateRestaurant
