import React from 'react'
import { useState } from 'react';
import { useParams } from 'react-router-dom'

const UpdateRestaurant = (props) => {

    const customStyle = {
        paddingLeft: 10,
        paddingBottom: 5,
        paddingTop: 5, 
    }
  
    const {id} = useParams();
    console.log(id);

    const [name, setName] = useState("");
    const [location, setLocation] = useState("");
    const [priceRange, setPriceRange] = useState("");

    return (
        <form>
            <div className='form-group'>
                <label style={customStyle} for="name">Name</label>
                <input value={name} onChange={e => setName(e.target.value)} type="text" class="form-control m-2" id="name"></input>
            </div>

            <div className='form-group'>
                <label style={customStyle} for="location">Location</label>
                <input  value={location} onChange={e => setName(e.target.value)} type="text" class="form-control m-2" id="location"></input>
            </div>

            <div className='form-group'>
                <label style={customStyle} for="price_range">Price Range</label>
                <input id="price_range" value={priceRange} onChange={e => setPriceRange(e.target.value)} type="number" class="form-control m-2"></input>
            </div>

            <button style={{marginTop: 10, marginLeft: 6}} className='btn btn-primary'>Submit</button>
        </form>
    )
}

export default UpdateRestaurant
