import React, { useState, useContext } from 'react'
import RestaurantFinder from '../apis/RestaurantFinder'
import { RestaurantsContext } from '../context/RestaurantContext'

const customStyle = {
    "paddingRight": 20
}

const AddRestaurant = () => {

    const {addRestaurants} = useContext(RestaurantsContext);
    const [name, setName] = useState("");
    const [location, setLocation] = useState("");
    const [priceRange, setPriceRange] = useState("Price Range");

    const handleSubmit = async (e) => {
        e.preventDefault();
        try{
            const response = await RestaurantFinder.post("/", {
                name,
                location,
                price_range: priceRange
            })
            addRestaurants(response.data.data.restaurant);
            console.log(response);
        }
        catch(err){}
    }

    return (
        <div style={customStyle}>
            <form onSubmit={handleSubmit}>
                <div className='row'>
                    <div className='form-group col'>
                        <input 
                        value={name}
                        onChange = {e => setName(e.target.value)}
                        className='form-control m-2' type='text' placeholder='Name'>
                        </input>
                    </div>

                    <div className='form-group col'>
                        <input 
                        value={location}
                        onChange={e => setLocation(e.target.value)}
                        className='form-control m-2' type='text' placeholder='Location'>
                        </input>
                    </div>

                    <div className='form-group col'>
                        <select 
                        value={priceRange} 
                        onChange={e => setPriceRange(e.target.value)} 
                        className='form-select m-2'>
                            <option defaultValue>Price Range</option>
                            <option value="1">$</option>
                            <option value="2">$$</option>
                            <option value="3">$$$</option>
                            <option value="4">$$$$</option>
                            <option value="5">$$$$$</option>
                        </select>
                    </div>

                    <div className="form-group col">
                        <button type="submit" className="btn btn-primary form-control m-2">Add</button>
                    </div>
                </div>
            </form>
        </div>
    )
}

export default AddRestaurant
